import React, {useState} from "react";
import {
    View,
    Text,
    FlatList,
    StyleSheet,
    Image,
    TouchableOpacity,
} from "react-native";

const DetailsScreen = () => {
    const [products, setProducts] = useState([
        {
            id: "",
            name: "",
            type: "",

            price: 10,
            numberCalories: 100,
            numberFats: 100,
            numberCarbohydrate: 100,
            numberProteins: 100,
            img: "",
        },
    ]);

    const renderProduct = ({item}) => (
        <View style={styles.product}>
            <Image
                source={{
                    uri: item.img,
                }}
                style={{
                    width: 300,
                    height: 300,
                }}
            />
            <Text style={styles.productName}>{item.name}</Text>
            <Text style={styles.productInfo}>
                Количество калорий: {item.numberCalories}
            </Text>
            <Text style={styles.productInfo}>
                Количество углеводов: {item.numberCarbohydrate}
            </Text>
            <Text style={styles.productInfo}>
                Количество жиров: {item.numberFats}
            </Text>
            <Text style={styles.productInfo}>
                Количество белков: {item.numberProteins}
            </Text>
            <Text style={styles.productPrice}>{item.price} USD</Text>
            <TouchableOpacity style={styles.addToCartButton}>
                <Text style={styles.addToCartButtonText}>Добавить в корзину</Text>
            </TouchableOpacity>
        </View>
    );

    return (
        <View style={styles.container}>
            <Text style={styles.title}>Список продуктов</Text>
            <FlatList
                data={products}
                renderItem={renderProduct}
                keyExtractor={(item) => item.id}
            />
        </View>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        padding: 20,
    },
    title: {
        fontSize: 24,
        fontWeight: "bold",
        marginBottom: 20,
    },
    product: {
        padding: 10,
        borderBottomWidth: 1,
        borderBottomColor: "#ccc",
    },
    productName: {
        fontSize: 18,
    },
    productInfo: {
        fontSize: 14,
    },
    productPrice: {
        fontSize: 16,
        color: "orange",
    },
    addToCartButton: {
        backgroundColor: "#8b00ff",
        padding: 10,
        borderRadius: 5,
        alignSelf: "flex-start",
    },
    addToCartButtonText: {
        color: "white",
        fontSize: 16,
        fontWeight: "bold",
    },
});

export default DetailsScreen;
